using BehaviorDesigner.Runtime.Tasks;using Character.Enemy.Combat;using UnityEngine;namespace Character.Enemy.AI{    public class AIAttackCommandCondition : Conditional    {        private EnemyCombatControl _enemyCombatControl;        public override void OnAwake()        {            _enemyCombatControl = GetComponent<EnemyCombatControl>();        }        public override TaskStatus OnUpdate()        {            return (_enemyCombatControl.GetAttackCommand()) ? TaskStatus.Success : TaskStatus.Failure;        }    }}