using BehaviorDesigner.Runtime.Tasks;using Character.Enemy.Combat;using Manager;using Unilts.Tools.DevelopmentTool;using UnityEngine;namespace Character.Enemy.AI{    public class AIAttackDistanceCondition : Conditional    {        private EnemyCombatControl _enemyCombatControl;        [SerializeField] private float _attackDistance;        public override void OnAwake()        {            _enemyCombatControl = GetComponent<EnemyCombatControl>();        }        public override TaskStatus OnUpdate()        {            //当敌人与玩家距离超过攻击距离 返回true            return (DevelopmentToos.DistanceForTarget(EnemyManager.MainInstance.GetMainPlayer(),                _enemyCombatControl.transform) > _attackDistance)                ? TaskStatus.Success                : TaskStatus.Failure;        }    }}