using BehaviorDesigner.Runtime.Tasks;using Character.Enemy.Combat;using UnityEngine;namespace Character.Enemy.AI{    public class AIComboAction : Action    {        private EnemyCombatControl _enemyCombatControl;        public override void OnAwake()        {            _enemyCombatControl = GetComponent<EnemyCombatControl>();        }        public override TaskStatus OnUpdate()        {            if (_enemyCombatControl.GetAttackCommand())            {                _enemyCombatControl.AIBaseAttackInput();                return TaskStatus.Running;            }            return TaskStatus.Success;        }    }}