using BehaviorDesigner.Runtime.Tasks;using Character.Enemy.Combat;using Character.Enemy.Move;using Manager;using Unilts.Tools.DevelopmentTool;using UnityEngine;namespace Character.Enemy.AI{    public class AIJustMoveForward : Action    {        private EnemyMoveControl _enemyMoveControl;        public override void OnAwake()        {            _enemyMoveControl = GetComponent<EnemyMoveControl>();        }        public override TaskStatus OnUpdate()        {            if (DevelopmentToos.DistanceForTarget(EnemyManager.MainInstance.GetMainPlayer(),                    _enemyMoveControl.transform) > 3.0f)            {                _enemyMoveControl.SetApplyMovement(true);                _enemyMoveControl.SetAnimatorMovementValue(0f , 1f);                return TaskStatus.Running;             }            return TaskStatus.Success;        }    }}