using Base;using Unity.VisualScripting;using UnityEngine;namespace Character.Player{    public class PlayerHealthControl : CharacterHealthBase    {        protected override void Awake()        {            base.Awake();            _characterHealthInfo = ScriptableObject.Instantiate(healthInfo);        }        protected override void Update()        {            base.Update();            PlayerParryInput();        }                protected override void CharacterHitAction(float damage, string hitName, string parryName)        {            Anim.Play(hitName, 0, 0f);        }        private void PlayerParryInput()        {                    }    }}